      }</script><div class="unsupported-browser-splash-screen hidden"><div class="center"><div class="logo-image-container"></div><h1>Oh no!</h1><h3>It looks like you're using an unsupported browser.</h3><h3>The site works well on modern versions of <a href="https://www.mozilla.org/en-US/firefox/new/">Firefox</a> , <a href="https://www.google.com/chrome/">Chrome</a> , <a href="https://www.microsoft.com/en-us/edge">Edge</a> , <a href="https://www.opera.com/">Opera</a> , or <a href="https://www.apple.com/lae/safari/">Safari</a> .</h3><h3>IE will not be supported.</h3><hr><div class="margin-md"><button class="link" onclick="if (!window.__cfRLUnblockHandlers) return false; appendSiteContent()">Try using the modern version anyway</button></div></div></div><script defer="defer" type="text/javascript">const isSupportedBrowser = function () {
        return window.CSS && CSS.supports('color', 'var(--primary)');
      };
      const showUnsupportedBrowserSplashScreen = function (shouldShow) {
        const unsupportedBrowserSplashScreen = document.querySelector(
                '.unsupported-browser-splash-screen'
        );
        if (shouldShow) {
          unsupportedBrowserSplashScreen.classList.remove('hidden');
        } else {
          unsupportedBrowserSplashScreen.classList.add('hidden');
        }
      };
      const appendSiteContent = function () {
        const mainElement = document.createElement('sc-site-layout');
        const body = document.querySelector('body');
        body.appendChild(mainElement);
        showUnsupportedBrowserSplashScreen(false);
      };
      const removeSiteContent = function () {
        const mainElement = document.querySelector('sc-site-layout');
        mainElement.parentNode.removeChild(mainElement);
      };
      if (!isSupportedBrowser()) {
        showUnsupportedBrowserSplashScreen(true);
        const logoImg = document.createElement('img');
        logoImg.src = 'img/android-chrome-192x192.png';
        document.querySelector('.logo-image-container').appendChild(logoImg);
        removeSiteContent();
      }
      document.addEventListener('metadata', function (e) {
        const metaData = e.detail;
        if (metaData) {
          const url = 'https://suttacentral.net' + window.location.pathname;
          document.head.querySelector('[rel=canonical]').href = url;
          document.head.querySelector('title').innerText = metaData.pageTitle;
          document.head.querySelector("[property='og:url']").content = url;
          document.head.querySelector("[property='og:title']").content = metaData.title;
          document.head.querySelector("[property='og:description']").content = metaData.description;
          document.head.querySelector("[property='og:type']").content = metaData.openGraphType
            ? metaData.openGraphType
            : 'website';
          document.head.querySelector("[name='title']").content = metaData.title;
          document.head.querySelector("[name='description']").content = metaData.description;
          document.head.querySelector("[name='twitter:title']").content = metaData.title;
          document.head.querySelector(
            "[name='twitter:description']"
          ).content = metaData.description.substring(0, 120);
          document.head.querySelector("[itemprop='name']").content = metaData.title;
          document.head.querySelector("[itemprop='description']").content = metaData.description;
        }
      });

      document.addEventListener('keyword-metadata', function (e) {
        const metaData = e.detail;
        if (metaData) {
          document.head.querySelector("[name='keywords']").content = metaData.keywords;
        }
      });

      window.addEventListener('beforeinstallprompt', function (e) {
        // Prevent Chrome 67 and earlier from automatically showing the prompt
        e.preventDefault();
        // Stash the event so it can be triggered later.
        window.deferredPWAInstallPrompt = e;
      });</script><noscript>Please enable JavaScript to view this website.</noscript></body></html>