  [lang='ta'] *,
  [lang='th'] *,
  [lang='xct'] *,
  [lang='zh'] * {
    font-style: normal;

    letter-spacing: normal;
    text-transform: none;

    font-synthesis: none;
    font-variant-caps: normal;
